import React from 'react';
import { Routes, Route } from 'react-router-dom';

function CustomerHome() {
  return (
    <div style={{ padding: 24, textAlign: 'center' }}>
      <h2>Welcome to Service Pronto</h2>
      <p>Scan a QR code to request staff assistance.</p>
      {/* In production, this would show location info from QR */}
      <button style={{ fontSize: 18, padding: '12px 32px', marginTop: 24 }}>Request Staff Assistance</button>
    </div>
  );
}

export default function CustomerApp() {
  return (
    <Routes>
      <Route path="/" element={<CustomerHome />} />
      {/* Future: /success, /error, etc. */}
    </Routes>
  );
}
