import React, { useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import axios from 'axios';

function CustomerHome() {
  const [status, setStatus] = useState(null);
  const [loading, setLoading] = useState(false);

  // For MVP, use a hardcoded locationId (e.g., 1) and random deviceId
  const locationId = 1;
  const deviceId = 'dev-' + Math.random().toString(36).substring(2, 10);

  const handleRequest = async () => {
    setLoading(true);
    setStatus(null);
    try {
      await axios.post('/api/requests', { locationId, deviceId });
      setStatus('success');
    } catch (err) {
      setStatus('error');
    }
    setLoading(false);
  };

  return (
    <div style={{ padding: 24, textAlign: 'center' }}>
      <h2>Welcome to Service Pronto</h2>
      <p>Scan a QR code to request staff assistance.</p>
      {/* In production, this would show location info from QR */}
      <button
        style={{ fontSize: 18, padding: '12px 32px', marginTop: 24 }}
        onClick={handleRequest}
        disabled={loading}
      >
        {loading ? 'Requesting...' : 'Request Staff Assistance'}
      </button>
      {status === 'success' && (
        <div style={{ color: 'green', marginTop: 20 }}>
          Help is on the way! A staff member has been notified.
        </div>
      )}
      {status === 'error' && (
        <div style={{ color: 'red', marginTop: 20 }}>
          Something went wrong. Please try again.
        </div>
      )}
    </div>
  );
}

export default function CustomerApp() {
  return (
    <Routes>
      <Route path="/" element={<CustomerHome />} />
      {/* Future: /success, /error, etc. */}
    </Routes>
  );
}
